/* on;line-mail */
.online {
  display: flex;
  margin-top: 20px;
}
.online-form {
  display: flex;
}
.online-form__input {
  padding-left: 16px;
  width: 358px;
  height: 50px;
  background-color: transparent;
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: 4px;
  outline: none;
  font-size: 16px;
  line-height: 20px;
  transition-property: border color;
  @extend %trans;
  color: var(--bcg-main);

  &::placeholder {
    font-size: 16px;
    line-height: 20px;
    color: rgba(255, 255, 255, 0.6);
    transition-property: color;
    transition-duration: var(--time);
    transition-timing-function: var(--functional-cubic);
  }
  &:hover::placeholder,
  &:focus::placeholder {
    color: var(--bcg-main);
  }
  &:hover,
  &:focus {
    border: 1px solid var(--bcg-main);
  }
