.online-form__button {
  @include centerFlex();
  width: 200px;
  height: 50px;
  margin-left: 12px;
  background-color: var(--theme-color);
  border: 0;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.15);
  border-radius: 4px;
  cursor: pointer;
}
.online-form__buttom-name {
  color: var(--text-secondary-liter);
