.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.2);
  opacity: 1;
  transition-property: opacity;
  @extend %trans;
  &.is-hidden {
    opacity: 0;
    pointer-events: none;
  }
}

.modal-container {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 528px;
  height: 581px;
  background-color: var(--bcg-main);
  padding: 40px;
  &__title {
    font-weight: 700;
    display: block;
    font-size: 20px;
    line-height: 1.15em;
    text-align: center;
    letter-spacing: 0.03em;
    color: var(--text-primary);
    margin-bottom: 12px;
  }
}

.modal-form__item + .modal-form__item {
  margin-top: 10px;
}



.modal-form__box {
  position: relative;
  display: flex;
  justify-content: center;
}
.modal-form__input {
  width: 100%;
  height: 40px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  border-radius: 4px;
  padding-left: 35px;
  transition-property: border-color;
  @extend %trans;
  .modal-form__item:hover &,
  .modal-form__item:focus & {
    outline: none !important;
    border-color: var(--theme-color);
    fill: var(--theme-color);
  }
  &:focus {
    outline: none !important;
    border-color: var(--theme-color);
    fill: var(--theme-color);
  }
}

.modal-form__icon {
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  transition-property: fill color;
  @extend %trans;
  .modal-form__item:hover &,
  .modal-form__item:focus & {
    outline: none !important;
    border-color: var(--theme-color);
    fill: var(--theme-color);
  }
  .modal-form__input:focus + & {
    outline: none !important;
    border-color: var(--theme-color);
    fill: var(--theme-color);
  }
}

.modal-form__text {
  display: block;
  height: 120px;
  padding: 16px;
  resize: none;
}

.modal-form__lable {
  display: block;
  margin-bottom: 4px;
  font-size: 12px;
  line-height: 1.16em;
  color: var(--text-secondary);
  /* identical to box height */
  letter-spacing: 0.01em;
}

.modal-form__checkbox {

  display: flex;
  justify-content: center;
  margin-top: 20px;
}

.modal-form__check-lable {
  display: flex;
  align-items: center;

  user-select: none;
  font-size: 14px;
  line-height: 1.36em;
  color: var(--text-secondary);
  letter-spacing: 0.03em;
}

.modal-form__check-lable::before {
  content: "";
  margin-right: 5px;
  display: block;
  width: 16px;
  height: 16px;
  border: 2px solid var(--text-logo);
  border-radius: 2px;
  transition-property: fill color background-color border background-image
    ackground-size background-repeat;
  transition-duration: var(--time);
  transition-timing-function: var(--functional-cubic);
}
.modal-form__check:hover + .modal-form__check-lable::before,
.modal-form__check:focus + .modal-form__check-lable::before {
  border: 2px solid var(--theme-color);
}

.modal-form__check:checked + .modal-form__check-lable::before {
  background-color: var(--theme-color);
  background-image: url(../images/accept.svg);
  background-position: center;
  background-size: auto;
  background-repeat: no-repeat;
  border: 0;
}

.modal-form__legal {
  /* display: block; */
  font-size: 14px;
  line-height: 24px;
  /* identical to box height, or 171% */

  letter-spacing: 0.03em;

  border-bottom: 1px solid var(--theme-color);

  color: var(--theme-color);
}
.modal-form__button {
  margin-left: auto;
  margin-right: auto;
  margin-top: 30px;
  background-color: var(--theme-color);
  width: 200px;
  height: 50px;
  border: 0;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.15);
  border-radius: 4px;
  cursor: pointer;
}
.modal-form__buttonname {
  color: var(--text-secondary-liter);
  font-size: 16px;
  font-weight: 700;
  font-family: var(--font-primary);
  line-height: 1.875;
  /* identical to box height, or 187% */
  align-items: center;
  text-align: center;
}

.modal-container__closed {
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  position: absolute;
  width: 30px;
  height: 30px;
  top: 8px;
  right: 8px;
  border-radius: 50%;
  border: solid 1px;
  background-color: transparent;
  border-color: var(--social-color-primary);
}
.modal-container__icon {
  width: 18px;
  height: 18px;

  fill: var(--text-logo);
  transition-property: fill;
  transition-duration: var(--time);
  transition-timing-function: var(--functional-cubic);
}

.modal-container__closed:hover .modal-container__icon,
.modal-container__closed:focus .modal-container__icon {
  fill: var(--theme-color);
}
